var gulp=require("gulp"),imageResize=require("gulp-image-resize"),changed=require("gulp-changed");gulp.task("gallery",function(){gulp.src("assets/img/gallery/*.{png,gif,jpg,JPG,JPEG}").pipe(changed("assets/img/gallery/crop/")).pipe(imageResize({width:1500,height:800,crop:!0,upscale:!0,quality:.5,format:"jpg"})).pipe(gulp.dest("assets/img/gallery/crop/"))}),gulp.task("thumbnail",function(){gulp.src("assets/img/thumbnail/*.{png,gif,jpg,JPG,JPEG}").pipe(changed("assets/img/thumbnail/crop/")).pipe(imageResize({width:500,height:500,crop:!0,upscale:!1,quality:.8,format:"jpg"})).pipe(gulp.dest("assets/img/thumbnail/crop/"))}),gulp.task("videoThumb",function(){gulp.src("assets/img/videoThumb/*.{png,gif,jpg,JPG,JPEG}").pipe(changed("assets/img/videoThumb/crop/")).pipe(imageResize({width:800,height:534,crop:!0,upscale:!1,quality:.7,format:"jpg"})).pipe(gulp.dest("assets/img/videoThumb/crop/"))}),gulp.task("featuredPriority",function(){gulp.src("assets/img/featuredPriority/*.{png,gif,jpg,JPG,JPEG}").pipe(changed("assets/img/featuredPriority/crop/")).pipe(imageResize({width:300,height:260,crop:!0,upscale:!0,quality:.7,format:"jpg"})).pipe(gulp.dest("assets/img/featuredPriority/crop/"))}),gulp.task("banner",function(){gulp.src("assets/img/banner/*.{png,gif,jpg,JPG,JPEG}").pipe(changed("assets/img/banner/crop/")).pipe(imageResize({width:1944,height:656,crop:!0,gravity:"Center",upscale:!0,quality:.7,format:"jpg"})).pipe(gulp.dest("assets/img/banner/crop/"))}),gulp.task("priority",function(){gulp.src("assets/img/priority/*.{png,gif,jpg,JPG,JPEG}").pipe(changed("assets/img/priority/crop/")).pipe(imageResize({width:1e3,height:500,crop:!0,gravity:"Center",upscale:!0,quality:.9,format:"jpg"})).pipe(gulp.dest("assets/img/priority/crop/"))}),gulp.task("default",["gallery","priority","thumbnail","videoThumb","featuredPriority","banner"]);